import subprocessimport sysdef install_dependencies():    dependencies = [        'selenium',        'webdriver-manager',        'dash',        'pandas',        'spacy',        'sentimental-onix',        'bertopic',        'sentence-transformers'    ]        for dep in dependencies:        subprocess.check_call([sys.executable, '-m', 'pip', 'install', dep])def run_additional_commands():    commands = [        [sys.executable, '-m', 'spacy', 'download', 'en_core_web_sm'],        [sys.executable, '-m', 'sentimental_onix', 'download', 'en']    ]        for cmd in commands:        subprocess.check_call(cmd)if __name__ == "__main__":    print("Installing dependencies...")    install_dependencies()        print("Running additional setup commands...")    run_additional_commands()        print("Setup completed successfully!")